__all__ = [
    'Token',
    'TokenType',
]


import enum

from typing import NamedTuple, Optional


class TokenType(enum.Enum):
    GROUP_START = 0
    GROUP_END = 1
    CONTROL = 2
    SYMBOL = 3
    TEXT = 4
    DESTINATION = 5
    IGNORABLE_DESTINATION = 6
    
    BINARY = 7



class Token(NamedTuple):
    
    raw: bytes
    
    type: TokenType
    
    
    name: Optional[bytes] = None
    
    
    parameter: Optional[int] = None
