class cPluginParent():
    macroOnly = False
    indexQuiet = False

plugins = []

def AddPlugin(cClass):
    global plugins

    plugins.append(cClass)



def CIC(expression):
    if callable(expression):
        return expression()
    else:
        return expression


def IFF(expression, valueTrue, valueFalse):
    if expression:
        return CIC(valueTrue)
    else:
        return CIC(valueFalse)

def P23Ord(value):
    if type(value) == int:
        return value
    else:
        return ord(value)

def P23Chr(value):
    if type(value) == int:
        return chr(value)
    else:
        return value
