














import base64


class BytesRule:
    

    def to_python(self, value, *, absent: bool = None):
        return value

    def to_proto(self, value):
        if isinstance(value, str):
            value = value.encode("utf-8")
            value += b"=" * (4 - len(value) % 4)  
            value = base64.urlsafe_b64decode(value)

        return value
