from enum import IntEnum


class ParamEnum(IntEnum):
    

    @classmethod
    def get_value(cls, item):
        
        try:
            return cls[item].value
        except KeyError:
            valid_options = {e.name for e in cls}
            raise ValueError(
                "'{}' is not a valid option, must be one of '{}'".format(
                    item, "', '".join(valid_options)
                )
            )
