

import shapely


def loads(data):
    
    return shapely.from_wkt(data)


def load(fp):
    
    data = fp.read()
    return loads(data)


def dumps(ob, trim=False, rounding_precision=-1, **kw):
    
    return shapely.to_wkt(ob, trim=trim, rounding_precision=rounding_precision, **kw)


def dump(ob, fp, **settings):
    
    fp.write(dumps(ob, **settings))
